 52	 public SeleniumGridUtil(SeleniumGridData seleniumGridData) {
 53	 this.seleniumGridData = seleniumGridData;
 54	 initialiseDockerClient();
 55	 this.headless = seleniumGridData.isHeadless();
 56	 if(this.headless) {
 57	 //If headless is expected. record video will be made as false.
 58	 seleniumGridData.setRecordVideo(false);
 59	 }
