 61	
 62	 private void initialiseDockerClient() {
 63	 if(isWindows()) {
 64	 // Configure to use Windows named pipes
 65	 DockerClientConfig config = DefaultDockerClientConfig.createDefaultConfigBuilder()
 66	 .withDockerHost("npipe:////./pipe/docker_engine")
 67	 // This tells Docker to use Linux containers
 68	 .withDockerTlsVerify(false)
 69	 .build();
 70	
 71	 // Use Apache HTTP client which supports named pipes on Windows
 72	 DockerHttpClient httpClient = new ApacheDockerHttpClient.Builder()
 73	 .dockerHost(config.getDockerHost())
 74	 .build();
 75	
 76	 dockerClient = DockerClientImpl.getInstance(config, httpClient);
 77	 } else {
 78	 dockerClient = DockerClientBuilder.getInstance().build();
 79	 }
 80	 }
...
 146	
 147	 public void launchGrid() {
 148	 initPorts();
 149	 if(seleniumGridData.isColimaToBeLaunched()) {
 150	 launchColima();
 151	 }
 152	 if(seleniumGridData.isRemoveContainersOlderThan24Hours()) {
 153	 removeContainersOlderThan24Hours();
 154	 }
 155	 if(seleniumGridData.isRemoveNetworkOlderThan24Hours()) {
 156	 stopOldContainersImagesNetwork();
 157	 }
 158	 if (hubContainerId == null) { //There could be multiple nodes getting launched - Chrome node, Firefox node etc. But a suite will have only one hub and one network. Once hub is launched, it shouldn't be launched again.
 159	 createNetwork();
 160	 pullAndCreateHubContainer();
 161	 }
 162	
 163	 //Wait for selenium grid is not needed now as we are launching hub separately, followed by node.
 164	 //waitForSeleniumGrid();
 165	 }
