 167	 public void launchNode() {
 168	 launchNode(seleniumGridData.getBrowser());
 169	 }
 170	
 171	 public void launchNode(Browser browser) {
 172	 if(hubPort == null) {
 173	 launchGrid();
 174	 }
 175	 pullAndCreateNodeContainer(browser);
 176	 if(seleniumGridData.isRecordVideo()) {
 177	 pullAndCreateVideoContainer();
 178	 }
 179	 }
