 263	 @SneakyThrows
 264	 private void pullAndCreateHubContainer() {
 265	 try {
 266	 HUB_NAME = "selenium-hub-" + hubPort;
 267	
 268	 //Pulling the docker image before creating container
 269	 System.out.println("Pulling Hub....");
 270	 dockerClient.pullImageCmd(SELENIUM_HUB_IMAGE_NAME)
 271	 .exec(new PullImageResultCallback())
 272	 .awaitCompletion();
 273	
 274	 // Port bindings for the hub
 275	 Ports portBindings = new Ports();
 276	 portBindings.bind(ExposedPort.tcp(4444),
 277	 Ports.Binding.bindPort(hubPort));
 278	 portBindings.bind(ExposedPort.tcp(4442),
 279	 Ports.Binding.bindPort(eventBusPublishPort));
 280	 portBindings.bind(ExposedPort.tcp(4443),
 281	 Ports.Binding.bindPort(eventBusSubscribePort));
