 62	 private void initialiseDockerClient() {
 63	 if(isWindows()) {
 64	 // Configure to use Windows named pipes
 65	 DockerClientConfig config = DefaultDockerClientConfig.createDefaultConfigBuilder()
 66	 .withDockerHost("npipe:////./pipe/docker_engine")
 67	 // This tells Docker to use Linux containers
 68	 .withDockerTlsVerify(false)
 69	 .build();
 70	
 71	 // Use Apache HTTP client which supports named pipes on Windows
 72	 DockerHttpClient httpClient = new ApacheDockerHttpClient.Builder()
 73	 .dockerHost(config.getDockerHost())
 74	 .build();
 75	
 76	 dockerClient = DockerClientImpl.getInstance(config, httpClient);
 77	 } else {
 78	 dockerClient = DockerClientBuilder.getInstance().build();
 79	 }
 80	 }
