 147	 public void launchGrid() {
 148	 initPorts();
 149	 if(seleniumGridData.isColimaToBeLaunched()) {
 150	 launchColima();
 151	 }
 152	 if(seleniumGridData.isRemoveContainersOlderThan24Hours()) {
 153	 removeContainersOlderThan24Hours();
