 156	 stopOldContainersImagesNetwork();
 157	 }
 158	 if (hubContainerId == null) { //There could be multiple nodes getting launched - Chrome node, Firefox node etc. But a suite will have only one hub and one network. Once hub is launched, it shouldn't be launched again.
 159	 createNetwork();
 160	 pullAndCreateHubContainer();
 161	 }
