 308	 private void pullAndCreateNodeContainer(Browser browser) {
 309	 try {
 310	 //Pulling the docker image before creating container
 311	 System.out.println("Pulling Node....");
 312	 currentVncPort.set(getNextAvailablePort());
 313	 String browserName = getBrowserName(browser);
 314	 SELENIUM_NODE_IMAGE_NAME = SELENIUM_NODE_IMAGE_NAME.replace("<BROWSER>", browserName);
 315	
 316	 dockerClient.pullImageCmd(SELENIUM_NODE_IMAGE_NAME)
 317	 .exec(new PullImageResultCallback())
 318	 .awaitCompletion();
 319	
 320	 //Allotting 2 GB for each node.
 321	 Long memoryAndShmSize = 2L * 1024 * 1024 * 1024;
 322	
 323	 // Environment variables for the node
 324	 Map<String, String> environmentVariables = getEnvironmentVariablesOfANode();
 325	
 326	 // Port bindings for the node
 327	 Ports nodePortBindings = new Ports();
 328	 nodePortBindings.bind(ExposedPort.tcp(7900),
 329	 Ports.Binding.bindPort(currentVncPort.get()));
 330	
 331	 // Create node container
 332	 System.out.println("Starting Node for " + browserName);
 333	
 334	 String uniqueNodeName = getUniqueNodeName();
 335	
 336	 CreateContainerResponse nodeContainer = dockerClient
 337	 .createContainerCmd(SELENIUM_NODE_IMAGE_NAME)
 338	 .withName(uniqueNodeName)
 339	 .withExposedPorts(ExposedPort.tcp(7900))
 340	 .withEnv(environmentVariables.entrySet().stream()
 341	 .map(e -> e.getKey() + "=" + e.getValue())
 342	 .toArray(String[]::new))
 343	 .withHostConfig(HostConfig.newHostConfig()
 344	 .withPortBindings(nodePortBindings)
 345	 .withLinks(new Link(HUB_NAME, "selenium-hub"))
 346	 .withRestartPolicy(RestartPolicy.onFailureRestart(3))
 347	 .withMemory(memoryAndShmSize) // 2GB / 4GB
 348	 .withShmSize(memoryAndShmSize)// 2GB / 4GB shm_size
 349	 .withNetworkMode(networkName)
 350	 .withBinds(new Bind(seleniumGridData.getDownloadFolderAbsolutePath(), new Volume(DOWNLOAD_PATH))))
 351	 .exec();
