 473	 private String getBrowserName(Browser browser) {
 474	 if(browser == null) {
 475	 browser = seleniumGridData.getBrowser();
 476	 }
 477	 switch (browser) {
 478	 case CHROME:
 479	 return isArmProcessor() ? "chromium" : "chrome";
 480	 case FIREFOX:
 481	 return "firefox";
 482	 case SAFARI:
 483	 return "safari";
 484	 case EDGE:
 485	 return "edge";
 486	 default:
 487	 throw new IllegalArgumentException("Unsupported browser: " + browser);
 488	 }
 489	 }
 490	}
