 17	 public static <T> T nvl(T value, T returnThisIfValueIsNull) {
 18	 return value == null ? returnThisIfValueIsNull : value;
 19	 }
 20	
 21	 public static <T> T safeEval(Supplier<T> supplier) {
 22	 try {
 23	 return supplier.get();
 24	 } catch (Exception e) {
 25	 return null;
 26	 }
 27	 }
 28	
 29	 public static boolean contains(String str1, String str2) {
 30	 if (str1 == null && str2 == null) {
 31	 return true;
 32	 }
 33	 if (str1 != null && str2 != null) {
 34	 if (str1.toLowerCase().trim().contains(str2.toLowerCase().trim())
 35	 || str2.toLowerCase().trim().contains(str1.toLowerCase().trim())) {
 36	 return true;
 37	 } else {
 38	 return false;
 39	 }
 40	 }
 41	 return false;
 42	 }
 43	
 44	 public static boolean isWindows() {
 45	 return System.getProperty("os.name").toLowerCase().startsWith("windows");
 46	 }
 47	
 48	 public static boolean isArmProcessor() {
 49	 return Stream.of("arm64", "aarch64").anyMatch(s -> System.getProperty("os.arch").contains(s));
 50	 }
 51	
 52	 public static List<String> executeCommand(String command) throws RuntimeException {
 53	 List<String> commandResponses = new ArrayList<>();
 54	 try {
 55	 System.out.println("Command : " + command);
 56	 Runtime rt = Runtime.getRuntime();
 57	 Process proc = rt.exec(command);
 58	 proc.waitFor(2, TimeUnit.MINUTES);
 59	 commandResponses.addAll(getCommandResponse(proc.getErrorStream()));
 60	 commandResponses.addAll(getCommandResponse(proc.getInputStream()));
 61	 System.out.println("Response : " + commandResponses);
 62	 } catch (Exception ex) {
 63	 ex.printStackTrace();
 64	 }
 65	 return commandResponses;
 66	 }
 67	
 68	 private static List<String> getCommandResponse(InputStream inputStream) {
 69	 List<String> commandResponses = new ArrayList<>();
 70	 try {
 71	 BufferedReader br = new BufferedReader(new InputStreamReader(inputStream));
 72	 String commandResponse = br.readLine();
 73	 while (commandResponse != null) {
 74	 commandResponses.add(commandResponse);
 75	 commandResponse = br.readLine();
 76	 }
 77	 } catch (Exception e) {
 78	 e.printStackTrace();
 79	 }
 80	 return commandResponses;
 81	 }
 82	
 83	 public static ZonedDateTime convertEpochToZonedDateTime(Long epochMillis) {
 84	 ZonedDateTime zdt = null;
 85	 try {
 86	 zdt = Instant.ofEpochMilli(epochMillis).atZone(ZoneId.systemDefault());
 87	 }catch(Exception ex)
 88	 {}
 89	 return zdt;
 90	 }
 91	}
