 253	 private void removeContainersOlderThan24Hours() {
 254	 try {
 255	 // Calculate timestamp for 24 hours ago
 256	 long twentyFourHoursAgo = Instant.now().minusSeconds(24 * 60 * 60).getEpochSecond();
 257	
 258	 // List all containers
 259	 List<Container> containers = dockerClient.listContainersCmd()
 260	 .withShowAll(true) // equivalent to docker ps -a
 261	 .exec();
 262	
 263	 // Filter and remove containers that are:
 264	 // 1. Older than 24 hours AND
 265	 // 2. Related to Selenium (hub or node)
 266	 containers.stream()
 267	 .filter(container -> container.getCreated() < twentyFourHoursAgo)
 268	 .filter(this::isSeleniumContainer)
 269	 .forEach(this::forceRemoveContainer);
 270	 } catch (Exception ex) {
 271	 ex.printStackTrace();
 272	 }
 273	 }
 274	
 275	 /**
 276	 * Checks if a container is a Selenium-related container
 277	 * @param container The container to check
 278	 * @return true if the container is a Selenium-related container
 279	 */
 280	 private boolean isSeleniumContainer(Container container) {
 281	 // Get the image name from the container
 282	 String imageId = container.getImage();
 283	
 284	 // Check if it's any Selenium-related container
 285	 return imageId != null && imageId.toLowerCase().contains("selenium");
 286	 }
 287	
 288	 private void forceRemoveContainer(Container container) {
 289	 try {
 290	 dockerClient.removeContainerCmd(container.getId())
 291	 .withForce(true) // Added force removal
 292	 .exec();
 293	 System.out.println("Removed container: " + container.getId());
 294	 } catch (Exception e) {
 295	 System.err.println("Failed to remove container: " + container.getId());
 296	 e.printStackTrace();
 297	 }
 298	 }
