 300	 private static int getNextAvailablePort() {
 301	 return PortProber.findFreePort();
 302	 }
 303	
 304	 @SneakyThrows
 305	 private void pullAndCreateHubContainer() {
 306	 try {
 307	 HUB_NAME = "selenium-hub-" + hubPort;
 308	
 309	 //Pulling the docker image before creating container
 310	 System.out.println("Pulling Hub....");
 311	 dockerClient.pullImageCmd(SELENIUM_HUB_IMAGE_NAME)
 312	 .exec(new PullImageResultCallback())
 313	 .awaitCompletion();
 314	
 315	 // Port bindings for the hub
 316	 Ports portBindings = new Ports();
 317	 portBindings.bind(ExposedPort.tcp(4444),
 318	 Ports.Binding.bindPort(hubPort));
 319	 portBindings.bind(ExposedPort.tcp(4442),
 320	 Ports.Binding.bindPort(eventBusPublishPort));
 321	 portBindings.bind(ExposedPort.tcp(4443),
 322	 Ports.Binding.bindPort(eventBusSubscribePort));
 323	
 324	 // Create hub container
 325	 System.out.println("Starting Hub....");
 326	 CreateContainerResponse hubContainer = dockerClient
 327	 .createContainerCmd(SELENIUM_HUB_IMAGE_NAME)
 328	 .withName(HUB_NAME)
 329	 .withExposedPorts(
 330	 ExposedPort.tcp(4444),
 331	 ExposedPort.tcp(4442),
 332	 ExposedPort.tcp(4443)
 333	 )
 334	 .withHostConfig(HostConfig.newHostConfig()
 335	 .withMemory(2147483648L)
 336	 .withShmSize(2147483648L)
 337	 .withRestartPolicy(RestartPolicy.onFailureRestart(3))
 338	 .withPortBindings(portBindings)
 339	 .withNetworkMode(networkName))
 340	 .exec();
 341	
 342	 hubContainerId = hubContainer.getId();
 343	 dockerClient.startContainerCmd(hubContainerId).exec();
 344	 } catch (Exception ex) {
 345	 ex.printStackTrace();
 346	 }
 347	 }
 348	
 349	 private void pullAndCreateNodeContainer(Browser browser) {
 350	 try {
 351	 //Pulling the docker image before creating container
 352	 System.out.println("Pulling Node....");
 353	 currentVncPort.set(getNextAvailablePort());
 354	 String browserName = getBrowserName(browser);
 355	 SELENIUM_NODE_IMAGE_NAME = SELENIUM_NODE_IMAGE_NAME.replace("<BROWSER>", browserName);
 356	
 357	 dockerClient.pullImageCmd(SELENIUM_NODE_IMAGE_NAME)
 358	 .exec(new PullImageResultCallback())
 359	 .awaitCompletion();
 360	
 361	 //Allotting 2 GB for each node.
 362	 Long memoryAndShmSize = 2L * 1024 * 1024 * 1024;
 363	
 364	 // Environment variables for the node
 365	 Map<String, String> environmentVariables = getEnvironmentVariablesOfANode();
 366	
 367	 // Port bindings for the node
 368	 Ports nodePortBindings = new Ports();
 369	 nodePortBindings.bind(ExposedPort.tcp(7900),
 370	 Ports.Binding.bindPort(currentVncPort.get()));
 371	
 372	 // Create node container
 373	 System.out.println("Starting Node for " + browserName);
 374	
 375	 String uniqueNodeName = getUniqueNodeName();
 376	
 377	 CreateContainerResponse nodeContainer = dockerClient
 378	 .createContainerCmd(SELENIUM_NODE_IMAGE_NAME)
 379	 .withName(uniqueNodeName)
 380	 .withExposedPorts(ExposedPort.tcp(7900))
 381	 .withEnv(environmentVariables.entrySet().stream()
 382	 .map(e -> e.getKey() + "=" + e.getValue())
 383	 .toArray(String[]::new))
 384	 .withHostConfig(HostConfig.newHostConfig()
 385	 .withPortBindings(nodePortBindings)
 386	 .withLinks(new Link(HUB_NAME, "selenium-hub"))
 387	 .withRestartPolicy(RestartPolicy.onFailureRestart(3))
 388	 .withMemory(memoryAndShmSize) // 2GB / 4GB
 389	 .withShmSize(memoryAndShmSize)// 2GB / 4GB shm_size
 390	 .withNetworkMode(networkName)
 391	 .withBinds(new Bind(seleniumGridData.getDownloadFolderAbsolutePath(), new Volume(DOWNLOAD_PATH))))
 392	 .exec();
 393	
 394	 nodeContainerId.set(nodeContainer.getId());
 395	 dockerClient.startContainerCmd(nodeContainer.getId()).exec();
 396	 String vncInfoMsg = "Please use " + getCurrentVncUrl() + " to check VNC";
 397	 System.out.println(vncInfoMsg);
 398	 } catch (Exception ex) {
 399	 ex.printStackTrace();
 400	 }
 401	 }
