 419	 private void pullAndCreateVideoContainer() {
 420	 try {
 421	 dockerClient.pullImageCmd(VIDEO_IMAGE_NAME)
 422	 .exec(new PullImageResultCallback())
 423	 .awaitCompletion();
 424	
 425	 String currentVideoName = VIDEO_CONTAINER_NAME + "_" + currentVncPort.get();
 426	 // Create a volume binding for /tmp/videos:/videos
 427	 Volume videoVolume = new Volume("/videos");
 428	 HostConfig hostConfig = HostConfig.newHostConfig()
 429	 .withNetworkMode(networkName)
 430	 .withBinds(new Bind(seleniumGridData.getVideoFolderAbsolutePath(), videoVolume))
 431	 .withRestartPolicy(RestartPolicy.onFailureRestart(3)); // Optional: adding similar restart policy as your node
 432	
 433	 //Define environment variables:
 434	 List<String> videoEnvVars = new ArrayList<>();
 435	 videoEnvVars.add("DISPLAY_CONTAINER_NAME=" + getUniqueNodeName()); // Link to specific node
 436	 videoEnvVars.add("FILE_NAME=" + currentVideoName + ".mp4"); // Set custom video filename
 437	
 438	 // Create the container
 439	 CreateContainerResponse videoContainer = dockerClient
 440	 .createContainerCmd(VIDEO_IMAGE_NAME)
 441	 .withName(currentVideoName)
 442	 .withEnv(videoEnvVars)
 443	 .withHostConfig(hostConfig)
 444	 .exec();
 445	
 446	 videoContainerId.set(videoContainer.getId());
 447	 // Start the container
 448	 dockerClient.startContainerCmd(videoContainer.getId()).exec();
 449	 System.out.println("Video gets saved in :\n" + seleniumGridData.getVideoFolderAbsolutePath());
 450	 } catch (Exception e) {
 451	 e.printStackTrace();
 452	 }
 453	 }
 454	
 455	 private Map<String, String> getEnvironmentVariablesOfANode() {
 456	 Map<String, String> environmentVariables = new HashMap<>();
 457	 environmentVariables.put("SE_EVENT_BUS_HOST", HUB_NAME);
 458	 environmentVariables.put("SE_EVENT_BUS_PUBLISH_PORT", "4442");
 459	 environmentVariables.put("SE_EVENT_BUS_SUBSCRIBE_PORT", "4443");
 460	 environmentVariables.put("SE_NODE_GRID_URL", "http://localhost:" + hubPort);
 461	 environmentVariables.put("SE_VNC_NO_PASSWORD", "1");
 462	 environmentVariables.put("SE_NODE_SESSION_TIMEOUT", "600");
 463	 environmentVariables.put("SE_NODE_MAX_SESSIONS", "1");
 464	 environmentVariables.put("SE_SCREEN_WIDTH", seleniumGridData.getScreenWidth());
 465	 environmentVariables.put("SE_SCREEN_HEIGHT", seleniumGridData.getScreenHeight());
 466	
 467	 if (headless) {
 468	 environmentVariables.put("SE_START_XVFB", "false");
 469	 }
 470	 return environmentVariables;
 471	 }
 472	
 473	 public void stopAndRemoveHubContainer() {
 474	 if (hubContainerId == null) {
 475	 return;
 476	 }
 477	 System.out.println("Stopping and removing hub container : " + hubContainerId);
 478	 dockerClient.stopContainerCmd(hubContainerId).exec();
 479	 dockerClient.removeContainerCmd(hubContainerId).withForce(true).exec();
 480	 }
 481	
 482	 public void stopAndRemoveNodeContainer() {
 483	 try {
 484	 if (nodeContainerId.get() == null) {
 485	 return;
 486	 }
 487	 System.out.println("Stopping and removing node container : " + nodeContainerId.get());
 488	 dockerClient.stopContainerCmd(nodeContainerId.get()).exec();
 489	 dockerClient.removeContainerCmd(nodeContainerId.get()).exec();
 490	 File videoFolder = new File(seleniumGridData.getVideoFolderAbsolutePath());
 491	 if (videoFolder.exists() && Objects.requireNonNull(videoFolder.listFiles()).length > 0) {
 492	 System.out.println("Video added in the path : " + videoFolder.getAbsolutePath());
 493	 }
 494	 } catch (Exception e) {
 495	 System.out.println("Unable to stop and remove node container : " + nodeContainerId.get() + "due to \n" + e.getMessage());
 496	 }
 497	 }
 498	
 499	 /**
 500	 * More cautious version that only removes dangling images
 501	 * (those with no repository or tag, often shown as <none>:<none>)
 502	 */
 503	 public void removeDanglingImages() {
 504	 int removedCount = 0;
 505	
 506	 try {
 507	 // Get only dangling images (images not tagged and not used)
 508	 List<Image> danglingImages = dockerClient.listImagesCmd()
 509	 .withDanglingFilter(true)
 510	 .exec();
 511	
 512	 // Remove dangling images
 513	 for (Image image : danglingImages) {
 514	 try {
 515	 String imageId = image.getId();
 516	 System.out.println("Removing dangling image: " + imageId + " created at " + convertEpochToZonedDateTime(image.getCreated()));
 517	 dockerClient.removeImageCmd(imageId).withForce(true).exec();
 518	 removedCount++;
 519	 } catch (Exception e) {
 520	 System.out.println("Could not remove dangling image: " + e.getMessage());
 521	 }
 522	 }
 523	
 524	 System.out.println("Successfully removed " + removedCount + " dangling images");
 525	 } catch (Exception e) {
 526	 System.err.println("Error while removing dangling images: " + e.getMessage());
 527	 e.printStackTrace();
 528	 }
 529	 }
 530	
 531	 @SneakyThrows
 532	 public URL getUrl() {
 533	 return new URL("http://localhost:" + getHubPort());
 534	 }
 535	
 536	 private String getBrowserName(Browser browser) {
 537	 if (browser == null) {
 538	 browser = seleniumGridData.getBrowser();
 539	 }
 540	 switch (browser) {
 541	 case CHROME:
 542	 return isArmProcessor() ? "chromium" : "chrome";
 543	 case FIREFOX:
 544	 return "firefox";
 545	 case SAFARI:
 546	 return "safari";
 547	 case EDGE:
 548	 return "edge";
 549	 case CHROMIUM:
 550	 return "chromium";
 551	 default:
 552	 throw new IllegalArgumentException("Unsupported browser: " + browser);
 553	 }
 554	 }
 555	}
