 63	 private SeleniumGridUtil(SeleniumGridData seleniumGridData) {
 64	 this.seleniumGridData = seleniumGridData;
 65	 initialiseDockerClient();
 66	 this.headless = seleniumGridData.isHeadless();
 67	 if(this.headless) {
 68	 //If headless is expected. record video will be made as false.
 69	 seleniumGridData.setRecordVideo(false);
 70	 }
 71	 if(seleniumGridData.isColimaToBeLaunched()) {
 72	 launchColima();
 73	 }
 74	 }
 75	
 76	 private void initialiseDockerClient() {
 77	 if(isWindows()) {
 78	 // Configure to use Windows named pipes
 79	 DockerClientConfig config = DefaultDockerClientConfig.createDefaultConfigBuilder()
 80	 .withDockerHost("npipe:////./pipe/docker_engine")
 81	 // This tells Docker to use Linux containers
 82	 .withDockerTlsVerify(false)
 83	 .build();
 84	
 85	 // Use Apache HTTP client which supports named pipes on Windows
 86	 DockerHttpClient httpClient = new ApacheDockerHttpClient.Builder()
 87	 .dockerHost(config.getDockerHost())
 88	 .build();
 89	
 90	 dockerClient = DockerClientImpl.getInstance(config, httpClient);
 91	 } else {
 92	 dockerClient = DockerClientBuilder.getInstance().build();
 93	 }
 94	 }
 95	
 96	 public void stopGridIfAvailable() {
 97	 if (hubContainerId == null) {
 98	 System.out.println("No Grid launched for this suite. Skipping stopGridIfAvailable()");
 99	 return;
 100	 }
 101	 // First stop all containers, then remove network
 102	 stopOldContainersImagesNetwork();
 103	 removeNetwork();
 104	 }
 105	
 106	 public void stopOldContainersImagesNetwork() {
 107	 if (seleniumGridData.isRemoveContainersOlderThan24Hours()) {
 108	 removeContainersOlderThan24Hours();
 109	 }
 110	 if (seleniumGridData.isRemoveDanglingImages()) {
 111	 removeDanglingImages();
 112	 }
 113	 if (seleniumGridData.isRemoveNetworkOlderThan24Hours()) {
 114	 removeNetworksOlderThan24Hours();
 115	 }
 116	 }
 117	
 118	 private void removeNetwork() {
 119	 try {
 120	 System.out.println("Removing docker network: " + networkName);
 121	 // First try to disconnect any remaining containers from the network
 122	 disconnectContainersFromNetwork(networkName);
 123	 // Then remove the network
 124	 dockerClient.removeNetworkCmd(networkName).exec();
 125	 System.out.println("Successfully removed network: " + networkName);
 126	 } catch (Exception ex) {
 127	 System.err.println("Failed to remove network: " + networkName + ", error: " + ex.getMessage());
 128	 ex.printStackTrace();
 129	 }
 130	 }
 131	
 132	 private void disconnectContainersFromNetwork(String networkName) {
 133	 try {
 134	 // Get network details to find connected containers
 135	 var network = dockerClient.inspectNetworkCmd().withNetworkId(networkName).exec();
 136	 if (network.getContainers() != null) {
 137	 network.getContainers().forEach((containerId, containerInfo) -> {
 138	 try {
 139	 System.out.println("Disconnecting container " + containerId + " from network " + networkName);
 140	 dockerClient.disconnectFromNetworkCmd()
 141	 .withNetworkId(networkName)
 142	 .withContainerId(containerId)
 143	 .exec();
 144	 } catch (Exception e) {
 145	 System.err.println("Failed to disconnect container " + containerId + " from network: " + e.getMessage());
 146	 }
 147	 });
 148	 }
 149	 } catch (Exception e) {
 150	 System.err.println("Failed to inspect network for disconnection: " + e.getMessage());
 151	 }
 152	 }
 153	
 154	 private void removeNetworksOlderThan24Hours() {
 155	 try {
 156	 // Calculate timestamp for 24 hours ago
 157	 long twentyFourHoursAgo = Instant.now().minusSeconds(24 * 60 * 60).getEpochSecond();
 158	
 159	 // List all networks
 160	 dockerClient.listNetworksCmd()
 161	 .exec()
 162	 .stream()
 163	 .filter(network -> {
 164	 // Filter networks created more than 24 hours ago
 165	 Date created = network.getCreated();
 166	 return created != null && created.toInstant().getEpochSecond() < twentyFourHoursAgo;
 167	 })
 168	 .filter(network -> contains(networkName + "_",network.getName()))
 169	 .forEach(network -> {
 170	 try {
 171	 System.out.println("Removing old network: " + network.getName() + " (created on " + network.getCreated() + ")");
 172	 dockerClient.removeNetworkCmd(network.getId()).exec();
 173	 } catch (Exception e) {
 174	 System.err.println("Failed to remove network: " + network.getName() + ", error: " + e.getMessage());
 175	 }
 176	 });
 177	
 178	 // Finally, prune any remaining unused networks
 179	 dockerClient.pruneCmd(PruneType.NETWORKS).exec();
 180	 System.out.println("Network cleanup completed");
 181	 } catch (Exception ex) {
 182	 System.err.println("Error during network cleanup: " + ex.getMessage());
 183	 ex.printStackTrace();
 184	 }
 185	 }
 186	
 187	 public void launchGrid() {
 188	 initPorts();
 189	 if(seleniumGridData.isRemoveContainersOlderThan24Hours()) {
 190	 removeContainersOlderThan24Hours();
 191	 }
 192	 if(seleniumGridData.isRemoveNetworkOlderThan24Hours()) {
 193	 stopOldContainersImagesNetwork();
 194	 }
 195	 if (hubContainerId == null) { //There could be multiple nodes getting launched - Chrome node, Firefox node etc. But a suite will have only one hub and one network. Once hub is launched, it shouldn't be launched again.
 196	 createNetwork();
 197	 pullAndCreateHubContainer();
 198	 }
 199	
 200	 //Wait for selenium grid is not needed now as we are launching hub separately, followed by node.
 201	 //waitForSeleniumGrid();
 202	 }
 203	
 204	 public void launchNode() {
 205	 launchNode(seleniumGridData.getBrowser());
 206	 }
 207	
 208	 public void launchNode(Browser browser) {
 209	 if(hubPort == null) {
 210	 launchGrid();
 211	 }
 212	 pullAndCreateNodeContainer(browser);
 213	 if(seleniumGridData.isRecordVideo()) {
 214	 pullAndCreateVideoContainer();
 215	 }
 216	 }
 217	
 218	 private void createNetwork() {
 219	 try {
 220	 networkName += "_" + hubPort;
 221	 dockerClient.createNetworkCmd()
 222	 .withName(networkName)
 223	 .withDriver(isWindows() ? "nat" : "bridge")
 224	 .exec();
 225	 System.out.println("Network for Grid is created : " + networkName);
 226	 } catch (Exception ex) {
 227	 ex.printStackTrace();
 228	 }
 229	 }
 230	
 231	 private void initPorts() {
 232	 try {
 233	 hubPort = getNextAvailablePort();
 234	 eventBusPublishPort = getNextAvailablePort();
 235	 eventBusSubscribePort = getNextAvailablePort();
 236	 System.out.println("Hub Port : " + hubPort);
 237	 System.out.println("Event Bus Publish Port : " + eventBusPublishPort);
 238	 System.out.println("Event Bus Subscribe Port : " + eventBusSubscribePort);
 239	 } catch (Exception ex) {
 240	 System.out.println("Unable to initialise Docker ports. Unable to proceed");
 241	 System.exit(1);
 242	 }
 243	 }
 244	
 245	 private void launchColima() {
 246	 if (isWindows()) {
 247	 System.out.println("No colima need to be launched for Windows");
 248	 } else {
 249	 executeCommand("colima start");
 250	 }
 251	 }
 252	
 253	 private void removeContainersOlderThan24Hours() {
 254	 try {
 255	 // Calculate timestamp for 24 hours ago
 256	 long twentyFourHoursAgo = Instant.now().minusSeconds(24 * 60 * 60).getEpochSecond();
 257	
 258	 // List all containers
 259	 List<Container> containers = dockerClient.listContainersCmd()
 260	 .withShowAll(true) // equivalent to docker ps -a
 261	 .exec();
 262	
 263	 // Filter and remove containers that are:
 264	 // 1. Older than 24 hours AND
 265	 // 2. Related to Selenium (hub or node)
 266	 containers.stream()
 267	 .filter(container -> container.getCreated() < twentyFourHoursAgo)
 268	 .filter(this::isSeleniumContainer)
 269	 .forEach(this::forceRemoveContainer);
 270	 } catch (Exception ex) {
 271	 ex.printStackTrace();
 272	 }
 273	 }
 274	
 275	 /**
 276	 * Checks if a container is a Selenium-related container
 277	 * @param container The container to check
 278	 * @return true if the container is a Selenium-related container
 279	 */
 280	 private boolean isSeleniumContainer(Container container) {
 281	 // Get the image name from the container
 282	 String imageId = container.getImage();
 283	
 284	 // Check if it's any Selenium-related container
 285	 return imageId != null && imageId.toLowerCase().contains("selenium");
 286	 }
 287	
 288	 private void forceRemoveContainer(Container container) {
 289	 try {
 290	 dockerClient.removeContainerCmd(container.getId())
 291	 .withForce(true) // Added force removal
 292	 .exec();
 293	 System.out.println("Removed container: " + container.getId());
 294	 } catch (Exception e) {
 295	 System.err.println("Failed to remove container: " + container.getId());
 296	 e.printStackTrace();
 297	 }
 298	 }
 299	
 300	 private static int getNextAvailablePort() {
 301	 return PortProber.findFreePort();
 302	 }
 303	
 304	 @SneakyThrows
 305	 private void pullAndCreateHubContainer() {
 306	 try {
 307	 HUB_NAME = "selenium-hub-" + hubPort;
 308	
 309	 //Pulling the docker image before creating container
 310	 System.out.println("Pulling Hub....");
 311	 dockerClient.pullImageCmd(SELENIUM_HUB_IMAGE_NAME)
 312	 .exec(new PullImageResultCallback())
 313	 .awaitCompletion();
 314	
 315	 // Port bindings for the hub
 316	 Ports portBindings = new Ports();
 317	 portBindings.bind(ExposedPort.tcp(4444),
 318	 Ports.Binding.bindPort(hubPort));
 319	 portBindings.bind(ExposedPort.tcp(4442),
 320	 Ports.Binding.bindPort(eventBusPublishPort));
 321	 portBindings.bind(ExposedPort.tcp(4443),
 322	 Ports.Binding.bindPort(eventBusSubscribePort));
 323	
 324	 // Create hub container
 325	 System.out.println("Starting Hub....");
 326	 CreateContainerResponse hubContainer = dockerClient
 327	 .createContainerCmd(SELENIUM_HUB_IMAGE_NAME)
 328	 .withName(HUB_NAME)
 329	 .withExposedPorts(
 330	 ExposedPort.tcp(4444),
 331	 ExposedPort.tcp(4442),
 332	 ExposedPort.tcp(4443)
 333	 )
 334	 .withHostConfig(HostConfig.newHostConfig()
 335	 .withMemory(2147483648L)
 336	 .withShmSize(2147483648L)
 337	 .withRestartPolicy(RestartPolicy.onFailureRestart(3))
 338	 .withPortBindings(portBindings)
 339	 .withNetworkMode(networkName))
 340	 .exec();
 341	
 342	 hubContainerId = hubContainer.getId();
 343	 dockerClient.startContainerCmd(hubContainerId).exec();
 344	 } catch (Exception ex) {
 345	 ex.printStackTrace();
 346	 }
 347	 }
 348	
 349	 private void pullAndCreateNodeContainer(Browser browser) {
 350	 try {
 351	 //Pulling the docker image before creating container
 352	 System.out.println("Pulling Node....");
 353	 currentVncPort.set(getNextAvailablePort());
 354	 String browserName = getBrowserName(browser);
 355	 SELENIUM_NODE_IMAGE_NAME = SELENIUM_NODE_IMAGE_NAME.replace("<BROWSER>", browserName);
 356	
 357	 dockerClient.pullImageCmd(SELENIUM_NODE_IMAGE_NAME)
 358	 .exec(new PullImageResultCallback())
 359	 .awaitCompletion();
 360	
 361	 //Allotting 2 GB for each node.
 362	 Long memoryAndShmSize = 2L * 1024 * 1024 * 1024;
 363	
 364	 // Environment variables for the node
 365	 Map<String, String> environmentVariables = getEnvironmentVariablesOfANode();
 366	
 367	 // Port bindings for the node
 368	 Ports nodePortBindings = new Ports();
 369	 nodePortBindings.bind(ExposedPort.tcp(7900),
 370	 Ports.Binding.bindPort(currentVncPort.get()));
 371	
 372	 // Create node container
 373	 System.out.println("Starting Node for " + browserName);
 374	
 375	 String uniqueNodeName = getUniqueNodeName();
 376	
 377	 CreateContainerResponse nodeContainer = dockerClient
 378	 .createContainerCmd(SELENIUM_NODE_IMAGE_NAME)
 379	 .withName(uniqueNodeName)
 380	 .withExposedPorts(ExposedPort.tcp(7900))
 381	 .withEnv(environmentVariables.entrySet().stream()
 382	 .map(e -> e.getKey() + "=" + e.getValue())
 383	 .toArray(String[]::new))
 384	 .withHostConfig(HostConfig.newHostConfig()
 385	 .withPortBindings(nodePortBindings)
 386	 .withLinks(new Link(HUB_NAME, "selenium-hub"))
 387	 .withRestartPolicy(RestartPolicy.onFailureRestart(3))
 388	 .withMemory(memoryAndShmSize) // 2GB / 4GB
 389	 .withShmSize(memoryAndShmSize)// 2GB / 4GB shm_size
 390	 .withNetworkMode(networkName)
 391	 .withBinds(new Bind(seleniumGridData.getDownloadFolderAbsolutePath(), new Volume(DOWNLOAD_PATH))))
 392	 .exec();
 393	
 394	 nodeContainerId.set(nodeContainer.getId());
 395	 dockerClient.startContainerCmd(nodeContainer.getId()).exec();
 396	 String vncInfoMsg = "Please use " + getCurrentVncUrl() + " to check VNC";
 397	 System.out.println(vncInfoMsg);
 398	 } catch (Exception ex) {
 399	 ex.printStackTrace();
 400	 }
 401	 }
 402	
 403	 public String getCurrentVncUrl() {
 404	 return "http://localhost:" + currentVncPort.get();
 405	 }
